var dataArray={people:{url:"https://swapi.dev/api/people/",parent:document.querySelector(".peopleBlock ul"),btnDownload:document.querySelector(".peopleBlock .download"),btnMore:document.querySelector(".peopleBlock .loadMore")},planets:{url:"https://swapi.dev/api/planets/",parent:document.querySelector(".planetBlock ul"),btnDownload:document.querySelector(".planetBlock .download"),btnMore:document.querySelector(".planetBlock .loadMore")},vehicles:{url:"https://swapi.dev/api/vehicles/",parent:document.querySelector(".vehicleBlock ul"),btnDownload:document.querySelector(".vehicleBlock .download"),btnMore:document.querySelector(".vehicleBlock .loadMore")}};function loadData(key){var res=dataArray[key];fetch(res.url).then(function(resData){return resData.json()}).then(function(result){showItems(result.results,res.parent);res.url=result.next;if(result.next){res.btnMore.classList.remove("hidden")}else{res.btnMore.classList.add("hidden")}})}function showItems(items,parent){items.forEach(function(item){var li=document.createElement("li");li.textContent=item.name;parent.appendChild(li);li.addEventListener("click",function(){return showDetails(item)})})}function showDetails(item){var modal=document.createElement("div");modal.className="modal";var detailsInfo="";for(var key in item){detailsInfo+="<p><b>".concat(key,"</b>: ").concat(item[key],"</p>")}modal.innerHTML="\n\t\t<div class=\"modal-content\">\n\t\t<span class=\"close\">&times;</span>\n\t\t<h3>".concat(item.name,"</h3>\n\t\t").concat(detailsInfo,"\n  \t</div>");document.body.appendChild(modal);modal.querySelector(".close").addEventListener("click",function(){return modal.remove()})}Object.keys(dataArray).forEach(function(key){var btnDownload=dataArray[key].btnDownload;var btnMore=dataArray[key].btnMore;btnDownload.addEventListener("click",function(){return loadData(key)});btnMore.addEventListener("click",function(){return loadData(key)})});